    for num_idx in 0..4 {
        for bit_pos in 0..8 {
            let shift_amount: u64 = 56 - (bit_pos * 8) as u64;
            output[output_index] = ((array[num_idx] >> shift_amount) & 255) as u8;
            output_index += 1;
        }
    }

    output
}

